const loginHandler=async a=>{a.preventDefault();const b=document.getElementById(`email`).value.trim(),c=document.getElementById(`password`).value.trim();if(b&&c){const a=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({email:b,password:c}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/library"):document.getElementById(`wrong`).innerHTML=`Couldn't find that user, please try again.`}};document.getElementById(`loginForm`)&&document.getElementById(`loginForm`).addEventListener(`submit`,loginHandler);