const newUserHandler=async a=>{a.preventDefault();const b=document.getElementById(`username`).value.trim(),c=document.getElementById(`email`).value.trim(),d=document.getElementById(`password`).value.trim();if(b&&c&&d){const a=await fetch("/api/users",{method:"POST",body:JSON.stringify({username:b,email:c,password:d}),headers:{"Content-Type":"application/json"}});console.log(a),a.ok?document.location.replace("/library"):document.getElementById(`wrong`).innerHTML=`Either email is in use, or password is less than 8 characters.`}};document.getElementById(`signUpForm`)&&document.getElementById(`signUpForm`).addEventListener(`submit`,newUserHandler);